sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    var statusCode;
    var result = {};
    var gcxValidationUtil = new GCXValidationUtil;
    var contact = request.body.data.contact;
    var sysId = request.pathParams.sysId;
    var errorList = [];

    var custContact = new GlideRecord('customer_contact');
    custContact.addQuery('sys_id', sysId);
    custContact.query();
    if (!custContact.next()) {
        result.contact = 'Invalid Sys_Id';
        statusCode = 422;
    } else {
        if (contact.firstName && (!gcxValidationUtil.validateContactName(contact.firstName))) {
            errorList.push("Invalid First Name");
        }
        if (contact.lastName && (!gcxValidationUtil.validateContactName(contact.lastName))) {
            errorList.push("Invalid Last Name");
        }
        if (contact.mobilePhone && (!gcxValidationUtil.validatePhoneNumber(contact.mobilePhone))) {
            errorList.push("Invalid Mobile Phone");
        }
        if (contact.homePhone && (!gcxValidationUtil.validatePhoneNumber(contact.homePhone))) {
            errorList.push("Invalid Home Phone");
        }
        if (contact.businessPhone && (!gcxValidationUtil.validatePhoneNumber(contact.businessPhone))) {
            errorList.push("Invalid Business Phone");
        }
        if (contact.otherPhone && (!gcxValidationUtil.validatePhoneNumber(contact.alternatePhone))) {
            errorList.push("Invalid Other Phone");
        }
        if (contact.email && (!gcxValidationUtil.validateEmail(contact.email))) {
            errorList.push("Invalid Email ID");
        }

        if (errorList.length > 0) {
            result.contact = errorList;
            statusCode = 422;
        } else {
            custContact.first_name = contact.firstName;
            custContact.last_name = contact.lastName;
            custContact.introduction = contact.prefix;
            custContact.gender = contact.gender;
            custContact.email = contact.email;
            custContact.sn_tmt_core_external_id = contact.externalId;
            custContact.home_phone = contact.homePhone;
            custContact.mobile_phone = contact.mobilePhone;
            custContact.phone = contact.businessPhone;
            custContact.u_alternate_phone = contact.alternatePhone;
            custContact.title = contact.title;
            custContact.street = contact.street;
            custContact.city = contact.city;
            custContact.state = contact.state;
            custContact.country = contact.countryCode;
            custContact.zip = contact.postalCode;
            custContact.u_written_language = contact.writtenLanguage;
            custContact.u_spoken_language = contact.spokenLanguage;
            custContact.update();
            result.contact = 'Contact updated successfully.';
            statusCode = 200;
        }

    }

    response.setBody(result);
    response.setStatus(statusCode);

})(request, response);
