sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    var req = request.body.data;
    var statusCode;
    var result = {};

    var location = req.location;
    var sysId = request.pathParams.sysId;

    var loc = new GlideRecord('cmn_location');
    loc.addQuery('sys_id', sysId);
    loc.query();
    if (!loc.next()) {
        result.location = 'Invalid Sys_Id';
        statusCode = 422;
    } else {

        loc.street = location.billingStreet ? location.billingStreet : location.shippingStreet;
        loc.city = location.billingCity ? location.billingCity : location.shippingCity;
        loc.state = location.billingState ? location.billingState : location.shippingState;
        loc.zip = location.billingPostalCode ? location.billingPostalCode : location.shippingPostalCode;
        loc.country = location.billingCountry ? location.billingCountry : location.shippingCountry;
        loc.longitude = location.billingCountry ? location.billingCountry : location.shippingLongitude;
        loc.latitude = location.billingLatitude ? location.billingLatitude : location.shippingLatitude;
        loc.update();
        result.location = 'Location updated successfully.';
        statusCode = 200;
    }

    response.setBody(result);
    response.setStatus(statusCode);

})(request, response);
