sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    var req = request.body.data;
    var errorList = [];
    var statuscode;
    var result = {};
    var gcxValidationUtil = new GCXValidationUtil;
    var account = req.account;
    var sysId = request.pathParams.sysId;


    try {
        var custAccount = new GlideRecord('customer_account');
        custAccount.addQuery('sys_id', sysId);
        custAccount.query();
        if (!custAccount.next()) {
            result.account = 'Invalid Sys_Id';
            statuscode = 422;
        } else {
            if (account.name && (!gcxValidationUtil.validateAccountName(account.name))) {
                errorList.push("Invalid Account Name");
            }
            if (account.phone && (!gcxValidationUtil.validatePhoneNumber(account.phone))) {
                errorList.push("Invalid Phone number");
            }

            if (errorList.length > 0) {
                result.account = errorList;
                statuscode = 422;
            } else {
                custAccount.name = account.name;
                custAccount.account_parent = account.parentAccount;
                custAccount.customer = account.customer;
                custAccount.fax_phone = account.faxPhone;
                custAccount.phone = account.phone;
                custAccount.sn_tmt_core_external_id = account.externalId;
                custAccount.website = account.website;
                custAccount.u_account_state = account.accountState;
                custAccount.u_currency_code = account.currencyCode;
                custAccount.u_gcx_entity = account.gcxEntity;
                custAccount.u_gcx_area = account.gcxArea;
                custAccount.u_gcx_region = account.gcxRegion;
                custAccount.update();
                result.body = 'Account updated successfully.';
                statuscode = 200;
            }
        }
    } catch (error) {
        response.setStatus(500);
        result.body = "Error occurred " + error.message;
        response.setBody(result);
        return;
    }
    response.setBody(result);
    response.setStatus(statuscode);

})(request, response);
